WiFiServer server(80);
typedef struct
{
    char wifi_name[80];
    char wifi_pass[80];
} AP_data_user;

String header;
AP_data_user AP_data;
void get_url_info(char *data, uint16_t index);
bool get_credentials();
void auto_wifi_connect();

void get_url_info(char *data, uint16_t index)
{
    for (uint8_t i = 0; i < 30; i++)
    {
        //I save the info in the struct
        if (header.charAt(index + i) == '&' || header.charAt(index + i) == ' ')
            break;
        else
            data[i] = header.charAt(index + i);
        if (data[i] == '+')
            data[i] = ' ';
        Serial.write(data[i]);
    }
}
bool get_credentials()
{
    WiFiClient client = server.available(); // listen for incoming clients

    if (client)
    {                                  // if you get a client,
        Serial.println("New Client."); // print a message out the serial port
        String currentLine = "";       // make a String to hold incoming data from the client
        while (client.connected())
        { // loop while the client's connected
            if (client.available())
            {                           // if there's bytes to read from the client,
                char c = client.read(); // read a byte, then
                header += c;
                if (c == '\n')
                {
                    if (currentLine.length() == 0)
                    {
                        client.println("HTTP/1.1 200 OK");
                        client.println("Content-type:text/html");
                        client.println();
                        Serial.println("________________________________");
                        Serial.println(header);
                        //GET /action_page.php?wifi_name=JoseLuis&wifi_pass=Todo+bien HTTP/1.1

                        if (header.indexOf("/action_page.php?wifi_name=") >= 0)
                        {
                            //GET /action_page.php?wifi_name=asd&wifi_pass=Fgh HTTP/1.1
                            Serial.println("__________START");
                            Serial.println(" ");
                            Serial.print("USER: ");
                            uint16_t index = header.indexOf("&wifi_name=") + 32;
                            get_url_info(AP_data.wifi_name, index);
                            Serial.println(" ");
                            Serial.print("PASS:");
                            index = header.indexOf("&wifi_pass=") + 11;
                            get_url_info(AP_data.wifi_pass, index);
                            Serial.println(" ");
                            Serial.println("__________END");
                            return true;
                        }
                        // the content of the HTTP response follows the header:
                        client.println(web_compleat);

                        // The HTTP response ends with another blank line:
                        client.println();
                        // break out of the while loop:
                        break;
                    }
                    else
                    { // if you got a newline, then clear currentLine:
                        currentLine = "";
                    }
                }
                else if (c != '\r')
                {                     // if you got anything else but a carriage return character,
                    currentLine += c; // add it to the end of the currentLine
                }

                // Check to see if the client request was "GET /H" or "GET /L":
                if (currentLine.endsWith("GET /H"))
                {
                    // digitalWrite(LED_RED, HIGH); // GET /H turns the LED_RED_RED on
                }
                if (currentLine.endsWith("GET /L"))
                {
                    // digitalWrite(LED_RED, LOW); // GET /L turns the LED_RED off
                }
            }
        }
        header = "";
        // close the connection:
        client.stop();
        // Serial.println("Client Disconnected.");
    }
    return false;
}
void auto_wifi_connect()
{

    int attemps = 0;

    Serial.println();
    Serial.println("Configuring access point");

    //I alocate the eeprom memmory:
    EEPROMClass eeprom;
    eeprom.begin(100);
    Serial.println(digitalRead(BUTTON_RESET));
    if (digitalRead(BUTTON_RESET) == LOW)
    {
        eeprom.writeString(0, "nop");
        eeprom.writeString(80, "nop");
        eeprom.commit();
    }
    eeprom.writeString(0, "RUBEN14");
    eeprom.writeString(80, "9472302440");
    Serial.println("----Alocated on flash:");
    String wifi_flash_memmory_user = eeprom.readString(0);
    String wifi_flash_memmory_pass = eeprom.readString(80);
    delay(1000);
    Serial.println(wifi_flash_memmory_user);
    Serial.println(wifi_flash_memmory_pass);

    //I try to connect to wifi if not:
    bool valid_credentials = true;
    WiFi.begin(wifi_flash_memmory_user.c_str(), wifi_flash_memmory_pass.c_str());
    delay(200);
    while (WiFi.status() != WL_CONNECTED)
    {
        delay(500);
        Serial.print(".");
        if (attemps++ > 20)
        {
            Serial.println("not valid credentials");
            valid_credentials = false;
            break;
        }
    }
    if (valid_credentials)
    {
        randomSeed(micros());
        Serial.println("");
        Serial.println("WiFi connected");
        Serial.println("IP address: ");
        Serial.println(WiFi.localIP());
    }
    else
    {
        const char *ssid = "Beacon_AP";
        const char *password = "beacon";
        Serial.println("Fail on connected with store flash data, genetate access point");
        WiFi.softAP("Beacon_AP", "beacon2020");
        IPAddress myIP = WiFi.softAPIP();
        Serial.print("AP IP address: ");
        Serial.println(myIP);
        server.begin();
        while (!get_credentials())
        {
        }
        Serial.println("i get the user:");
        for (int i = 0; i < sizeof(AP_data.wifi_name); i++)
            Serial.write(AP_data.wifi_name[i]);
        Serial.println("");
        Serial.println("i get the pass:");
        for (int i = 0; i < sizeof(AP_data.wifi_pass); i++)
            Serial.write(AP_data.wifi_pass[i]);
        Serial.println("");
        WiFi.softAPdisconnect(true);
        // We start by connecting to a WiFi network
        Serial.println();
        Serial.print("Connecting to ");
        Serial.println(AP_data.wifi_name);
        WiFi.begin(AP_data.wifi_name, AP_data.wifi_pass);
        attemps = 0;
        while (WiFi.status() != WL_CONNECTED)
        {
            delay(500);
            Serial.print(".");
            if (attemps++ > 15)
                esp_restart();
        }
        //I save on the flash of the device:
        eeprom.writeString(0, AP_data.wifi_name);
        eeprom.writeString(80, AP_data.wifi_pass);
        eeprom.commit();

        Serial.println("");
        Serial.println("WiFi connected");
        Serial.println("IP address: ");
        Serial.println(WiFi.localIP());
    }
}
